import React, { Component, Suspense, lazy} from 'react';

// Router Components
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom';

// Style
import '../style/App.css';

// Import Components
import Header from './Header';
// const Home = lazy(() => import('./Home'));
// const About = lazy(() => import('./About'));
// const Projects = lazy(() => import('./Projects'));

// import Body from './Body';
// import Header from './Header';
// import Footer from './Footer';

const routes = [
  {
    name: "home",
    path: "/",
    exact: true,
    nav: () => <Header index={0} />,
    component: () => <Home />
  },
  {
    name: "projects",
    path: "/projects",
    exact: true,
    nav: () => <Header index={1} />,
    component: () => <Projects />
  },
  {
    name: "about",
    path: "/about",
    exact: true,
    nav: () => <Header index={2} />,
    component: () => <About />
  }
]

class App extends Component {
  render() {
    return (
      <ThemeProvider theme={theme}>
        <Router>
          <Switch>
            {routes.map((route, index) => (
              <Route
                key={index}
                name={route.name}
                path={route.path}
                exact={route.exact}
                children={route.nav}
              />
            ))}
          </Switch>

          <Suspense fallback={<div>Loading...</div>}>
            <Switch>
              {routes.map((route, index) => (
                <Route
                  key={index}
                  name={route.name}
                  path={route.path}
                  exact={route.exact}
                  component={route.component}
                />
              ))}
            </Switch>
          </Suspense>
        </Router>
      </ThemeProvider>
    );
  }
}

