import React, {Component} from 'react';
import Row from 'react-bootstrap/Row';

class Projects extends Component {
  constructor(props) {
    super(props);
    this.state = {
      repos: [],
      repoName: '',
      isLoaded: false,
      error: null
    };
  }

  componentDidMount() {
    fetch('https://api.github.com/users/calebdsharp/repos')
      .then(response => response.json())
        .then(data => {
          this.setState({
            repos: data,
            isLoaded: true
          })
        })
    .catch(error => console.error(error))
  }

  backgroundChange() {
    let x = document.getElementsByClassName('repos');
    let i;
    for(i = 0; i < x.length; i++) {
      x[i].style.backgroundSize = 'cover';
      x[i].style.backgroundColor = 'pink';
    }
  }

  returnBg() {
  if (viewportWidth > 678) {
    var x = document.getElementsByClassName('returnBg');
    var i;
    for (i = 0; i < x.length; i++) {
      x[i].style.backgroundImage = "none";
      x[i].style.backgroundColor = "inherit";
    }
  }
}
  render() {
    const { error, isLoaded, repos } = this.state;

    const filter = repos.filter(repo => {
      if (repo.name.includes('amok') || repo.name.includes('smart') || repo.name.includes('full-stack') || repo.name.includes('Fortune')) {
        return repo.name;
      }
    });

    const displayRepos = filter.map((repo, i) => {
      return <div className="repos" key={repo.id}><a href={repo.html_url} onMouseOver={this.backgroundChange} >{repo.name}</a></div>
    });

    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    }

    return (
      <div className="projects">
        {displayRepos}
      </div>
    );
  }
}
export default Projects;
