import React, {Component} from 'react';

class Projects extends Component {
  constructor(props) {
    super(props);
    this.state = {
      repos: [],
      isLoaded: false,
      error: null
    };
  }

   componentDidMount() {
    fetch('https://api.github.com/users/calebdsharp/repos')
    .then(
      (result) => {
      this.setState({
        isLoaded: true,
        repos: result
      });
    },
    (error)=> {
      this.setState({
        isLoaded: true,
        error
      });
    }
  )}

  render() {
    const { error, isLoaded, repos } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
          {repos.map(repo => (
            <li key={repo.name}>
              {repo.name}
            </li>
          ))}
        </ul>
      );
    }
  }
}
export default Projects;
