import React, {Component} from 'react';
import Spinner from 'react-bootstrap/Spinner'

class Photos extends Component {
  constructor(props) {
    super(props);
    this.state = {
      photos: [],
      isLoaded: false,
      error: null
    };
  }

  componentDidMount() {
    setTimeout(() => {
      this.setState({isLoaded: true})
    }, 1000);
    // fetch('https://graph.instagram.com/17841400631260357/media?access_token=IGQVJWSURzMkdyQWw3eWNzalBVdWlZAanFNZAnZAyOEQ2cHY0WW5PSTV6NHNnMkxrMGJJdTBDalNjaFl2c0cxZAFdvNHNvN190Ukw5dUdCSENzcGJwX0NtRWpna0NaMVF4bzdzQ2drb1R3&fields=id,timestamp,media_url').then(response => response.json()).then(data => {
    //   this.setState({photos: data.data, isLoaded: true});
    // }).catch(err => this.setState({error: err.message}))
  }

  renderPhotos = () => {
    if (this.state.error) {
      return <div>Error: {this.state.error}</div>;
    } else if (!this.state.isLoaded) {
      return <Spinner animation="grow"/>;
    } else {
      return (<ul className="pic-list">
        {
          this.state.photos.map((pic, i) => {
            let caption = pic.caption;
            return (<li classname="photos" key={pic.id}>
              <figure>
                <img classname="pic" src={pic.media_url} width="150px" height="150px"/>
                <figcaption>{caption}</figcaption>
              </figure>
            </li>)
          })
        }
      </ul>);
    }
  }

  render() {
    const {error, photos, isLoaded} = this.state;

    let renderContainer = false;

    if (isLoaded) {
      renderContainer =
      <React.Fragment>
        {this.renderPhotos()}
      </React.Fragment>
    }

    return (<React.Fragment>
      {renderContainer}
    </React.Fragment>);
  }
}

export default Photos;
