        return (
            <div>
                <ul className="pic-list">
                    {this.state.photos.map((pic, i) => {
                        let caption = pic.caption;
                        return (
                            <li classname="photos" key={pic.id}>
                                <figure>
                                    <img classname="pic" src={pic.media_url} width="300px" height="300px" />
                                    <figcaption>{caption}</figcaption>
